import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet } from 'react-native';
import { initializeApp } from "firebase/app";
import { getFirestore, collection, query, where, getDocs, updateDoc, doc, arrayUnion } from 'firebase/firestore';

const firebaseConfig = {
  apiKey: "AIzaSyCjsh6Mj0fxTwcd5rwbk11ow3UATgpwrw8",
  authDomain: "pantry4you-bf048.firebaseapp.com",
  projectId: "pantry4you-bf048",
  storageBucket: "pantry4you-bf048.appspot.com",
  messagingSenderId: "951653716590",
  appId: "1:951653716590:web:a5c9df4baaf8b9fef2cc7f",
  measurementId: "G-MV5KGDBTTJ"
};

const app = initializeApp(firebaseConfig);
const db = getFirestore(app);

const uid = "gM5RarV9VAej9eJgQZ66fjSnZN43";

function Search() {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  function handleSearch() {
    // Make sure search query is not empty or invalid
    if (!searchQuery || !/\S+@\S+\.\S+/.test(searchQuery)) {
      setSearchResults([]);
      return;
    }

    // Query Firestore for users with matching emails
    const q = query(collection(db, 'users'), where('email', '==', searchQuery.toLowerCase()));
    getDocs(q)
      .then(querySnapshot => {
        if (!querySnapshot.empty) {
          const matchingUsers = [];
          querySnapshot.forEach(doc => {
            const userData = doc.data();
            matchingUsers.push({ email: userData.email, uid: doc.id });
          });
          setSearchResults(matchingUsers);
        } else {
          setSearchResults([]);
        }
      })
      .catch(error => {
        console.error(error);
      });
  }

  function handleAddFriend(uid) {
    const userDocRef = doc(db, 'users', uid);
    const friendsField = doc(db, 'users', uid, 'friends');

    updateDoc(userDocRef, {
      friends: arrayUnion(friendsField),
    })
      .then(() => {
        console.log('Friend added successfully');
      })
      .catch(error => {
        console.error(error);
      });
  }

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Search for Friends</Text>
      <TextInput
        style={styles.input}
        placeholder="Enter friend's email"
        value={searchQuery}
        onChangeText={text => setSearchQuery(text)}
      />
      <TouchableOpacity style={styles.button} onPress={handleSearch}>
        <Text style={styles.buttonText}>Search</Text>
      </TouchableOpacity>
      {searchResults.length > 0 ? (
        <View style={styles.resultsContainer}>
          <Text style={styles.resultsText}>Results:</Text>
          {searchResults.map(({ email, uid }, index) => (
            <View key={index} style={styles.resultContainer}>
              <Text style={styles.resultEmail}>{email}</Text>
              <TouchableOpacity style={styles.addButton} onPress={() => handleAddFriend(uid)}>
                <Text style={styles.addButtonText}>Add Friend</Text>
              </TouchableOpacity>
            </View>
          ))}
        </View>
      ) : (
        <Text style={styles.noResults}>{!searchQuery ? 'Please provide a valid email' : 'Sorry, there are no users with that email'}</Text>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 24,
  },
  input: {
    width: '80%',
    height: 40,
    borderWidth: 1,
    borderColor: 'gray',
    borderRadius: 8,
    paddingHorizontal: 16,
    marginBottom: 16,
  },
  resultsContainer: {
    marginTop: 24,
    width: '80%',
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 8,
    paddingVertical: 16,
    paddingHorizontal: 16,
    backgroundColor: '#f9f9f9',
  },
  result: {
    fontSize: 16,
    marginVertical: 8,
  },
  addFriendButton: {
    paddingHorizontal: 16,
    paddingVertical: 8,
    borderRadius: 8,
    backgroundColor: '#4caf50',
    marginTop: 8,
  },
  addFriendButtonText: {
    color: 'white',
    fontWeight: 'bold',
    textAlign: 'center',
  },
  noResults: {
    marginTop: 24,
    color: 'gray',
  },
});

export { Search };
