import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet } from 'react-native';
import firebase from 'firebase/app';
import 'firebase/auth';

function Search() {
  const [email, setEmail] = useState('');
  const [results, setResults] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');

  function handleSearch() {
    setErrorMessage('');
    if (!email || !email.trim()) {
      setErrorMessage('Sorry, please provide a valid email');
      return;
    }
    const db = firebase.auth();
    db.fetchSignInMethodsForEmail(email)
      .then((signInMethods) => {
        if (signInMethods.length > 0) {
          setResults([{ email: email }]);
        } else {
          setResults([]);
          setErrorMessage('Sorry, there are no users with that email');
        }
      })
      .catch((error) => {
        console.log(error);
        setResults([]);
        setErrorMessage('Sorry, an error occurred');
      });
  }

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Search for Friends</Text>
      <TextInput
        style={styles.input}
        placeholder="Email"
        value={email}
        onChangeText={(text) => setEmail(text.toLowerCase())}
      />
      <TouchableOpacity onPress={handleSearch}>
        <Text style={styles.button}>Search</Text>
      </TouchableOpacity>
      {errorMessage ? <Text style={styles.error}>{errorMessage}</Text> : null}
      {results.map((user, index) => (
        <Text key={index}>{user.email}</Text>
      ))}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 24,
  },
  input: {
    width: '80%',
    height: 40,
    borderWidth: 1,
    borderColor: 'gray',
    borderRadius: 8,
    paddingHorizontal: 16,
    marginBottom: 16,
  },
  button: {
    backgroundColor: 'blue',
    color: 'white',
    paddingHorizontal: 16,
    paddingVertical: 8,
    borderRadius: 8,
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 16,
  },
  error: {
    color: 'red',
    marginBottom: 16,
  },
});

export default Search;
