import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet } from 'react-native';
import firebase from 'firebase/app';
import 'firebase/database';

function Search() {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  function handleSearch() {
    // Make sure search query is not empty or invalid
    if (!searchQuery || !/\S+@\S+\.\S+/.test(searchQuery)) {
      setSearchResults([]);
      return;
    }

    // Query Firebase Realtime Database for users with matching emails
    const db = firebase.database();
    const usersRef = db.ref('users');
    usersRef.orderByChild('email').startAt(searchQuery.toLowerCase()).endAt(searchQuery.toLowerCase() + '\uf8ff').once('value')
      .then(snapshot => {
        if (snapshot.exists()) {
          const matchingUsers = [];
          snapshot.forEach(userSnapshot => {
            const userData = userSnapshot.val();
            matchingUsers.push(userData.email);
          });
          setSearchResults(matchingUsers);
        } else {
          setSearchResults([]);
        }
      })
      .catch(error => {
        console.error(error);
      });
  }

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Search for Friends</Text>
      <TextInput
        style={styles.input}
        placeholder="Enter friend's email"
        value={searchQuery}
        onChangeText={text => setSearchQuery(text)}
      />
      <TouchableOpacity onPress={handleSearch}>
        <Text>Search</Text>
      </TouchableOpacity>
      {searchResults.length > 0 ? (
        <View style={styles.resultsContainer}>
          {searchResults.map((email, index) => (
            <Text key={index} style={styles.result}>{email}</Text>
          ))}
        </View>
      ) : (
        <Text style={styles.noResults}>{!searchQuery ? 'Please provide a valid email' : 'Sorry, there are no users with that email'}</Text>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 24,
  },
  input: {
    width: '80%',
    height: 40,
    borderWidth: 1,
    borderColor: 'gray',
    borderRadius: 8,
    paddingHorizontal: 16,
    marginBottom: 16,
  },
  resultsContainer: {
    marginTop: 24,
  },
  result: {
    fontSize: 16,
    marginVertical: 8,
  },
  noResults: {
    marginTop: 24,
    color: 'gray',
  },
});

export { Search };
